module.exports = function(sequelize, DataTypes) {
    return sequelize.define('user', {
        username    : DataTypes.STRING, // definitely needed
        email       : DataTypes.STRING, // might be needed
        async_token : DataTypes.STRING
    }, { underscored: true });
}